import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./work.css";


class Work extends Component {
    constructor() {
        super();
        this.state = { 
            time: {}, 
            seconds: 18 
        };
        this.timer = 0;
        this.startTimer = this.startTimer.bind(this);
        this.countDown = this.countDown.bind(this);
      }

      secondsToTime(secs){
        let hours = Math.floor(secs / (60 * 60));

        let divisor_for_minutes = secs % (60 * 60);
        let minutes = Math.floor(divisor_for_minutes / 60);

        let divisor_for_seconds = divisor_for_minutes % 60;
        let seconds = Math.ceil(divisor_for_seconds);

        let obj = {
          "h": hours,
          "m": minutes,
          "s": seconds
        };
        return obj;
      }

      componentDidMount() {
        let timeLeftVar = this.secondsToTime(this.state.seconds);
        this.setState({ time: timeLeftVar });
      }

      startTimer() {
        if (this.timer === 0 && this.state.seconds > 0) {
          this.timer = setInterval(this.countDown, 1000);
        }
      }
      countDown() {
        // Remove one second, set state so a re-render happens.
        let seconds = this.state.seconds - 1;
        this.setState({
          time: this.secondsToTime(seconds),
          seconds: seconds,
        });

        // Check if we're at zero.
        if (seconds === 0) { 
          clearInterval(this.timer);
        }
      }    

    render() {

        return (
            <div class="bg-dark">
        <div class="jumbotron jumbotron-fluid">
            <div class="container">
                <h1 class="display-4">Work!</h1>
                <p class="lead text-danger">Throw your combos as they're called out!</p>
            </div>
        </div>
        <div class="container">
            <div class="row">
                <div className="round col-sm-4 text-light">
                    <button className="bg-success text-light rounded" onClick={this.startTimer}>Start</button>
                </div>
            </div>
        </div>

        <div class="row">
            <div className="col-6">
                <div className="card card1">
                    <div className="card-body">
                        <h5 class="card-title">Current Combo</h5>
                        <p class="card-text">Jab</p>
                        <p class="card-text">Cross</p>
                        <p class="card-text">Hook</p>
                    </div>
                </div>
            </div>
            <div className="col-6">
                <div className="card card2">
                    <div className="card-body">
                        <h5 class="card-title">Total Strikes:</h5>
                        <br/>
                        <p class="card-text">23</p>
                    </div>
                </div>
            </div>
            <br/>
            <div class="container">
                <div class="row">
                    <div className="round col-sm-4 text-light">
                        <h3>Round {this.state.round} of 5</h3>
                    </div>
                </div>
                <div class="row">
                <div className="round col-sm-4 text-light">
                    <h2>{this.state.time.m}:{this.state.time.s}</h2>
                    <Link to="/Stats"><button className="bg-danger text-light rounded">Finish</button></Link>
                </div>
                </div>
            </div>
        </div>
    </div>
        )
    }
}





export default Work;
