import React, { Component } from "react";
import "./style.css"



class Style extends Component {

    state = {
        style: "",
    }



    render() {
        console.log('state=====', this.state)
        return (
            <form>
                <h3>What is your goal for today's workout?</h3>
                <input
                name="style"
                type="radio"
                value="boxing"
                checked={this.state.style === "boxing"}
                onChange={this.handleInputChange}
                />
                <label>Boxing</label>
                <br />

                <input
                name="style"
                type="radio"
                value="kickboxing"
                checked={this.state.style === "kickboxing"}
                onChange={this.handleInputChange}
                />
                <label>Kickboxing</label>
                <br />

                <input
                name="style"
                type="radio"
                value="mma"
                checked={this.state.style === "mma"}
                onChange={this.handleInputChange}
                />
                <label>MMA</label>
            </form>
        )
    };
};

export default Style;
