import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./work.css";


class Work extends Component {
    constructor() {
        super();
   this.state = {
     seconds: '00', 
     minutes: '3',
     round: '5'
   }
   // method that triggers the countdown functionality
   this.startCountDown = this.startCountDown.bind(this);
   this.tick = this.tick.bind(this);
   }
   tick() {
   var min = Math.floor(this.secondsRemaining / 60);
   var sec = this.secondsRemaining - (min * 60);
   var round = 5;
   this.setState({
     minutes: min,
     seconds: sec,
     round: round
   })
   if (sec < 10) {
     this.setState({
       seconds: "0" + this.state.seconds,
     })
   }
   if (min < 10) {
   this.setState({
     value: "0" + min,
    })
   }
   if (min === 0 & sec === 0) {
   clearInterval(this.intervalHandle);
//    change round
   }
   this.secondsRemaining--
   }
   startCountDown() {
   this.intervalHandle = setInterval(this.tick, 1000);
   let time = this.state.minutes;
   this.secondsRemaining = time * 60;
   }

    render() {
        return (
            <div class="bg-dark">
        <div class="jumbotron jumbotron-fluid">
            <div class="container">
                <h1 class="display-4">Work!</h1>
                <p class="lead text-danger">Throw your combos as they're called out!</p>
            </div>
        </div>
        <div class="container">
            <div class="row">
                <div className="round col-sm-4 text-light">
                    <button className="bg-success text-light rounded" onClick={this.startCountDown}>Start</button>
                </div>
            </div>
        </div>

        <div class="row">
            <div className="col-6">
                <div className="card card1">
                    <div className="card-body">
                        <h5 class="card-title">Current Combo</h5>
                        <p class="card-text">Jab</p>
                        <p class="card-text">Cross</p>
                        <p class="card-text">Hook</p>
                    </div>
                </div>
            </div>
            <div className="col-6">
                <div className="card card2">
                    <div className="card-body">
                        <h5 class="card-title">Total Strikes:</h5>
                        <br/>
                        <p class="card-text">23</p>
                    </div>
                </div>
            </div>
            <br/>
            <div class="container">
                <div class="row">
                    <div className="round col-sm-4 text-light">
                        <h3>Round {this.state.round} of 5</h3>
                    </div>
                </div>
                <div class="row">
                <div className="round col-sm-4 text-light">
                    <h2>{this.state.minutes}:{this.state.seconds}</h2>
                    <Link to="/Stats"><button className="bg-danger text-light rounded">Finish</button></Link>
                </div>
                </div>
            </div>
        </div>
    </div>
        )
    }
}





export default Work;
